head.script:
// pops up a window with either a GB or London or Paris map
function mapOpen(mapfile, mapwidth, mapheight) {
    mapwin = window.open(mapfile, 'Map', 'toolbar=no,location=no,directories=no,status=yes,menubar=no,scrollbars=no,resizable=no,width=' + mapwidth + ',height=' + mapheight + ',align=center,screenX=100,screenY=100,left=100,top=100'); 
    mapwin.focus()
    }
